mapper.update:
update node
set name = #{name,jdbcType=VARCHAR},
  lft = #{lft,jdbcType=BIGINT},
  rgt = #{rgt,jdbcType=BIGINT},
  creat_time = #{creatTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

update node set
rgt = rgt + 2
where rgt >= #{rgt}

update node set
lft = lft + 2
where lft >= #{rgt}
